class Solution {
    public int nthMagicalNumber(int K, int A, int B) {
        long low = 0, high = 2000000000000000000L;
        int G = A*B/gcd(A, B);
        while(high - low > 1){
        	long h = high+low>>1;
        	long num = h/A+h/B-h/G;
        	if(num >= K){
        		high = h;
        	}else{
        		low = h;
        	}
        }
        return (int)(high%1000000007);
    }

	public int gcd(int a, int b) {
		while (b > 0) {
			int c = a;
			a = b;
			b = c % b;
		}
		return a;
	}
}

class Solution(object):
    def gcd(self, a, b):
        if 0 == b:
            return a
        return self.gcd(b, a % b)

    def nthMagicalNumber(self, n, a, b):
        """
        :type N: int
        :type A: int
        :type B: int
        :rtype: int
        """
        c = a * b / self.gcd(a, b)
        lo, hi = 1, 1 << 60
        while lo < hi:
            mid = (lo + hi) / 2
            t = mid / a + mid / b - mid / c
            if t < n:
                lo = mid + 1
            else:
                hi = mid
        return lo % 1000000007

