const int mod = 1000000007;
class Solution {
public:
  int nthMagicalNumber(int N, int A, int B) {
    ll l = 1, r = (ll)1e18, L = lcm(A, B);
    while (l <= r) {
      ll m = (l + r) / 2;
      ll cnt = m / A + m / B - m / L;
      if (cnt >= N) r = m - 1;
      else l = m + 1;
    }
    return l % mod;
  }
};

class Solution:
    def nthMagicalNumber(self, N, A, B):
        """
        :type N: int
        :type A: int
        :type B: int
        :rtype: int
        """
        MOD = 10 ** 9 + 7

        if A > B:
            A, B = B, A
        if B % A == 0:
            return (A * N) % MOD

        def gcd(x, y):
            if x % y == 0:
                return y
            return gcd(y, x % y)

        C = A * B // gcd(A, B)

        def count(X):
            ans = 0
            ans += X // A
            ans += X // B
            ans -= X // C
            return ans

        l, r = 1, N * min(A, B)
        while l < r:
            mid = (l + r) // 2
            if count(mid) < N:
                l = mid + 1
            else:
                r = mid
        return l % MOD

