class Solution:
    def reverseOddLevels(self, root: Optional[TreeNode]) -> Optional[TreeNode]:
        stuff = defaultdict(list)
        def dfs(node, d):
            if node:
                stuff[d].append(node)
                dfs(node.left, d+1)
                dfs(node.right, d+1)

        dfs(root, 0)
        for k in stuff:
            if k & 1:
                row = stuff[k]
                vals = [n.val for n in row]
                vals = vals[::-1]
                for i, n in enumerate(row):
                    n.val = vals[i]
        return root

/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    vector<int> v[30];
    void dfs(TreeNode* u, int d=0) {
        if (!u)
            return;
        if (d%2)
            v[d].push_back(u->val);
        dfs(u->left, d+1);
        dfs(u->right, d+1);
    }
    void dfs2(TreeNode* u, int d=0) {
        if (!u)
            return;
        if (d%2) {
            u->val=v[d].back();
            v[d].pop_back();
        }
        dfs2(u->left, d+1);
        dfs2(u->right, d+1);
    }
    TreeNode* reverseOddLevels(TreeNode* root) {
        dfs(root);
        dfs2(root);
        return root;
    }
};

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public TreeNode reverseOddLevels(TreeNode root) {
        Queue<TreeNode> queue = new ArrayDeque<TreeNode>();
        queue.offer(root);
        int level = -1;
        while (!queue.isEmpty()) {
            level++;
            List<TreeNode> list = new ArrayList<TreeNode>();
            int size = queue.size();
            for (int i = 0; i < size; i++) {
                TreeNode node = queue.poll();
                list.add(node);
                if (node.left != null)
                    queue.offer(node.left);
                if (node.right != null)
                    queue.offer(node.right);
            }
            if (level % 2 == 1)
                reverse(list);
        }
        return root;
    }

    public void reverse(List<TreeNode> list) {
        int left = 0, right = list.size() - 1;
        while (left < right) {
            TreeNode node1 = list.get(left), node2 = list.get(right);
            int val1 = node1.val, val2 = node2.val;
            node1.val = val2;
            node2.val = val1;
            left++;
            right--;
        }
    }
}

/**
 * Definition for a binary tree node.
 * function TreeNode(val, left, right) {
 *     this.val = (val===undefined ? 0 : val)
 *     this.left = (left===undefined ? null : left)
 *     this.right = (right===undefined ? null : right)
 * }
 */
/**
 * @param {TreeNode} root
 * @return {TreeNode}
 */
var reverseOddLevels = function(root) {
    let q = [root]
    let level = 0
    const odds = []
    while (q.length) {
        const nq = []
        if (level % 2) {
            odds.push([...q])
        }
        for (const cur of q) {
            if (cur.left) nq.push(cur.left)
            if (cur.right) nq.push(cur.right)
        }
        q = nq
        level++
    }
    for (const odd of odds) {
        for (let i = 0, j = odd.length - 1; i < j; i++, j--) {
            const t = odd[i].val 
            odd[i].val = odd[j].val
            odd[j].val = t
        }
    }
    return root
};

class Solution {
public:
  int slidingPuzzle(vector<vector<int>> &board) {
    using State = vector<vector<int>>;
    queue<State> q;
    q.push(board);
    map<State, int> f;
    State ending{{1, 2, 3}, {4, 5, 0}};
    f[board] = 0;
    int dx[] = {0, 1, 0, -1}, dy[] = {1, 0, -1, 0};
    while (!q.empty()) {
      auto s = q.front();
      q.pop();
      if (s == ending) {
        return f[s];
      }
      int x, y;
      for (int i = 0; i < 2; ++i) {
        for (int j = 0; j < 3; ++j) {
          if (s[i][j] == 0) {
            x = i;
            y = j;
          }
        }
      }
      for (int i = 0; i < 4; ++i) {
        int xx = x + dx[i], yy = y + dy[i];
        if (0 <= xx && xx < 2 && 0 <= yy && yy < 3) {
          auto v = s;
          swap(v[xx][yy], v[x][y]);
          if (!f.count(v)) {
            q.push(v);
            f[v] = f[s] + 1;
          }
        }
      }
    }
    return -1;
  }
};

class Solution {
    public int slidingPuzzle(int[][] board) {
        int[] a = new int[6];
        for(int i = 0;i < 2;i++){
        	for(int j = 0;j < 3;j++){
        		a[i*3+j] = board[i][j];
        	}
        }
        int to = encPerm(new int[]{1, 2, 3, 4, 5, 0});

        int[] d = new int[720];
        Queue<int[]> q = new ArrayDeque<>();
        q.add(a);
        Arrays.fill(d, 9999999);
        d[encPerm(a)] = 0;
        int[] dr = { 1, 0, -1, 0 };
		int[] dc = { 0, 1, 0, -1 };
        while(!q.isEmpty()){
        	int[] cur = q.poll();
        	int ec = encPerm(cur);
        	for(int i = 0;i < 6;i++){
        		if(cur[i] == 0){
        			int r = i/3, c = i % 3;
        			for(int k = 0;k < 4;k++){
        				int nr = r + dr[k], nc = c + dc[k];
        				if(nr >= 0 && nr < 2 && nc >= 0 && nc < 3){
        					int ne = nr*3+nc;
        	        		int[] nex = Arrays.copyOf(cur, 6);
        	        		int dum = nex[i]; nex[i] = nex[ne]; nex[ne] = dum;
        	        		int e = encPerm(nex);
        	        		if(d[e] > d[ec] + 1){
        	        			d[e] = d[ec] + 1;
        	        			q.add(nex);
        	        		}
        				}
        			}
        		}
        	}
        }
        if(d[to] > 99999)return -1;
        return d[to];
    }

	public int encPerm(int[] a)
	{
		int n = a.length;
		int used = 0;
		int ret = 0;
		for(int i = 0;i < n;i++){
			ret = ret * (n - i) + a[i] - Integer.bitCount(used & ((1<<a[i]) - 1));
			used |= 1<<a[i];
		}
		return ret;
	}
}

class Solution:
	def encode(self, a):
		ret = ''
		for i in range(2):
			for j in range(3):
				ret += '{}'.format(a[i][j])
		return ret

	def decode(self, val):
		a = [[None, None, None], [None, None, None]]
		t = 0
		for i in range(2):
			for j in range(3):
				a[i][j] = int(val[t])
				t += 1
		return a

	def copy(self, val):
		a = [[None, None, None], [None, None, None]]
		for i in range(2):
			for j in range(3):
				a[i][j] = val[i][j]
		return a

	def slidingPuzzle(self, board):
		"""
		:type board: List[List[int]]
		:rtype: int
		"""
		start = self.encode(board)

		dist = {}
		dist[start] = 0
		import collections
		q = collections.deque()
		q.append(start)
		while '123450' not in dist:
			if len(q) == 0:
				return -1

			idx = q.popleft()
			cur = self.decode(idx)

			for i in range(2):
				for j in range(3):
					if cur[i][j] == 0:
						x, y = i, j

			# print(cur, cur)
			if x > 0:
				tmp = self.copy(cur)
				tmp[x][y], tmp[x - 1][y] = tmp[x - 1][y], tmp[x][y]
				# print(cur, '1', tmp)
				tmp = self.encode(tmp)
				if tmp not in dist:
					dist[tmp] = dist[idx] + 1
					q.append(tmp)
			if x < 1:
				tmp = self.copy(cur)
				tmp[x][y], tmp[x + 1][y] = tmp[x + 1][y], tmp[x][y]
				# print(cur, '2', tmp)
				tmp = self.encode(tmp)
				if tmp not in dist:
					dist[tmp] = dist[idx] + 1
					q.append(tmp)
			if y > 0:
				tmp = self.copy(cur)
				tmp[x][y], tmp[x][y - 1] = tmp[x][y - 1], tmp[x][y]
				# print(cur, '3', tmp)
				tmp = self.encode(tmp)
				if tmp not in dist:
					dist[tmp] = dist[idx] + 1
					q.append(tmp)
			if y < 2:
				tmp = self.copy(cur)
				tmp[x][y], tmp[x][y + 1] = tmp[x][y + 1], tmp[x][y]
				# print(cur, '4', tmp)
				tmp = self.encode(tmp)
				if tmp not in dist:
					dist[tmp] = dist[idx] + 1
					q.append(tmp)






		return dist['123450']

