class Solution {
    int o[256],f[9][256],a[9];
public:
    int maxStudents(vector<vector<char>>& seats) {
        int n=seats.size(),m=seats[0].size(),i,j,k,ans=0;
        for(i=1;i<256;i++)o[i]=o[i>>1]+(i&1);
        memset(f,128,sizeof(f));
        memset(a,0,sizeof(a));
        for(i=0;i<n;i++)for(j=0;j<m;j++)if(seats[i][j]=='#')a[i]|=1<<j;
        for(i=f[0][0]=0;i<n;i++)for(j=0;j<1<<m;j++)if(!(j&a[i])&&!(j&j>>1)&&!(j&j<<1))for(k=0;k<1<<m;k++)if(!(j&k>>1)&&!(j&k<<1))f[i+1][j]=max(f[i+1][j],f[i][k]+o[j]);
        for(i=0;i<1<<m;i++)ans=max(ans,f[n][i]);
        return ans;
    }
};

class Solution(object):
    def maxStudents(self, A):
        R, C = len(A), len(A[0])
        for r, row in enumerate(A):
            for c, val in enumerate(row):
                if val == '.':
                    A[r][c] = True
                else:
                    A[r][c] = False

        # dp on state transitions
        legal = [[] for _ in xrange(R)]
        for cand in itertools.product((0, 1), repeat=C):
            if any(cand[i] and cand[i+1] for i in xrange(C-1)):
                continue
            for r in xrange(R):
                if any(cand[i] and not A[r][i] for i in xrange(C)):
                    continue
                legal[r].append(cand)

        dp = {}
        for cand in legal[0]:
            dp[cand] = sum(cand)

        for r in xrange(1, R):
            dp2 = {}
            for cand, s in dp.items():
                for cand2 in legal[r]:
                    s2 = sum(cand2)
                    v2 = dp2.get(cand2, -1)
                    if v2 >= s+s2: continue
                    for i in xrange(C):
                        if cand[i]:
                            if i-1>=0 and cand2[i-1] or i+1<C and cand2[i+1]:
                                break
                    else:
                        dp2[cand2] = s2 + s

            dp = dp2

        return max(dp.values())

class Solution {
    public int maxStudents(char[][] map) {
        int n = map.length, m = map[0].length;
        int[] dp = new int[1<<m];
        Arrays.fill(dp, -9999999);
        dp[0] = 0;
        for(int i = n-1;i >= 0;i--){
        	int[] ndp = new int[1<<m];
        	inner:
        	for(int j = 0;j < 1<<m;j++){
        		int b = j>>>1|j<<1;
        		if((b&j) != 0)continue;
        		for(int k = 0;k < m;k++){
        			if(j<<~k<0){
        				if(map[i][k] == '#')continue inner;
        			}
        		}
        		int val = -9999999;
	        	for(int f = 0;f < 1<<m;f++){
	        		int ng = f<<1|f>>>1;
	        		if((ng&j) == 0){
	        			val = Math.max(val, dp[f]);
	        		}
	        	}
	        	val += Integer.bitCount(j);
	        	ndp[j] = val;
        	}
        	dp = ndp;
        }
        int ans = 0;
        for(int v : dp)ans = Math.max(ans, v);
        return ans;
    }
}

class Solution:
    def maxStudents(self, seats: List[List[str]]) -> int:

        storage = []

        for index,row in enumerate(seats):
            storage.append(dict())

            allrows = self.generateAllRows(row)
            for arangement in allrows:
                if len(storage)==1:
                    storage[-1][arangement] = len(arangement)
                else:
                    nogood = set()
                    for taken in arangement:
                        nogood.add(taken+1)
                        nogood.add(taken-1)
                        if taken in nogood:
                            good = False
                    bestprevrow = 0
                    for prevarangement in storage[-2]:
                        good = True
                        for taken in prevarangement:
                            if taken in nogood:
                                good = False
                                break
                        if good:
                            if storage[-2][prevarangement]>bestprevrow:
                                bestprevrow = storage[-2][prevarangement]
                    storage[-1][arangement] = len(arangement)+bestprevrow

        return max(storage[-1].values())


    def generateAllRows(self,row,pos = 0): # returns a list of format [[a,b,c],...] where a,b and c are indecies where students can sit
        if len(row)<=pos:
            return [tuple()]
        if row[pos] == '#':
            return self.generateAllRows(row,pos+1)
        else:
            temp = self.generateAllRows(row,pos+1)
            temp2 = []
            for a in temp:
                if pos+1 not in a:
                    temp2.append(a+(pos,))
            return temp+temp2

/**
 * @param {character[][]} seats
 * @return {number}
 */
var maxStudents = function(seats) {
    const memo = new Map()
    const M = seats.length
    const N = seats[0].length
    let ans = 0
    for(const arr of arrange(M - 1)) {
        ans = Math.max(ans, dfs(M - 1, arr))
    }
    return ans

    function dfs(row, arr) {
        const key = row + arr
        if (memo.has(key)) return memo.get(key)

        const count = [...arr].reduce((sum, num) => sum + +num, 0)
        let ans = count
        for(const tarr of arrange(row - 1)) {
            if (valid(arr, tarr)) {
                ans = Math.max(
                    ans,
                    dfs(row - 1, tarr) + count
                )
            }
        }
        memo.set(key, ans)
        return ans
    }

    function valid(arr, tarr) {
        for(let i = 0; i < N; i++) {
            if (arr[i] === '1') {
                if (tarr[i - 1] === '1' || tarr[i + 1] === '1') return false
            }
        }
        return true
    }

    function* arrange(row, begin = 0) {
        if (row < 0) return
        if (begin === N) {
            yield ''
            return
        }
        for(const rest of arrange(row, begin + 1)) {
            yield '0' + rest
            if (seats[row][begin] === '.' && rest[0] !== '1') {
                yield '1' + rest
            }
        }
    }
};

int lowbit(int x) {
    return (x & (-x));
}

bool check(int x) {
    if ((x & (x << 1)) > 0) return true;
    if ((x & (x >> 1)) > 0) return true;
    return false;
}

int maxStudents(char** seats, int seatsSize, int* seatsColSize){
    int cnt[256];
    int max_y = (1 << (*seatsColSize)) - 1;
    cnt[0] = 0;
    for (int i = 1; i <= max_y; i++) {
        cnt[i] = cnt[i - lowbit(i)] + 1;
    }
    int f[8][256] = {0};
    int s[8] = {0};
    for (int i = 0; i < seatsSize; i++) {
        for (int j = 0; j < *seatsColSize; j++) {
            if (seats[i][j] == '.') {
                s[i] |= (1 << j);
            }
        }
    }
    for (int i = 0; i <= max_y; i++) if ((i | s[0]) == s[0]){
        if (!check(i)) {
            f[0][i] = cnt[i];
            //printf("A%d %d\n", i, f[0][i]);
        }
    }
    for (int i = 1; i < seatsSize; i++) {
        for (int j = 0; j <= max_y; j++) if ((j | s[i]) == s[i] && (!check(j))) {
            for (int k = 0; k <= max_y; k++) if ((k | s[i - 1]) == s[i - 1] && (!check(k))){
                if (((k & (j << 1)) == 0) && ((k & (j >> 1)) == 0)) {
                    if (f[i - 1][k] + cnt[j] > f[i][j]) {
                        f[i][j] = f[i - 1][k] + cnt[j];
                    }
                }
            }
        }
    }
    int ans = 0;
    for (int i = 0; i <= max_y; i++) {
        if (f[seatsSize - 1][i] > ans) {
            ans = f[seatsSize - 1][i];
        }
    }
    return ans;
}

