class Solution {
    public int maxSideLength(int[][] a, int threshold) {
        int n = a.length, m = a[0].length;
        long[][] b = cum(a);
        int ret = 0;
        for(int d = 1;d <= n && d <= m;d++){
        	for(int i = 0;i+d <= n;i++){
        		for(int j = 0;j+d <= m;j++){
        			if(b[i+d][j+d] - b[i+d][j] - b[i][j+d] + b[i][j] <= threshold){
        				ret = d;
        			}
        		}
        	}
        }
        return ret;
    }

	public long[][] cum(int[][] a)
	{
		int n = a.length, m = a[0].length;
        long[][] cum = new long[n+1][m+1];
        for(int i = 0;i < n;i++){
        	for(int j = 0;j < m;j++){
        		cum[i+1][j+1] = cum[i+1][j] + cum[i][j+1] - cum[i][j] + a[i][j];
        	}
        }
        return cum;
	}
}

class Solution(object):
    def maxSideLength(self, mat, threshold):
        """
        :type mat: List[List[int]]
        :type threshold: int
        :rtype: int
        """
        n, m = len(mat), len(mat[0])
        s = [[0]*(m+1) for _ in xrange(n+1)]
        for i, row in enumerate(mat):
            for j, v in enumerate(row):
                s[i+1][j+1] = mat[i][j]+s[i+1][j]+s[i][j+1]-s[i][j]
        a, b = 0, min(n, m)+1
        while a+1<b:
            c=(a+b)>>1
            t = min(s[i+c][j+c]+s[i][j]-s[i][j+c]-s[i+c][j]
                    for i in xrange(1+n-c)
                    for j in xrange(1+m-c))
            if t <= threshold: a=c
            else: b=c
        return a

class Solution:
    def maxSideLength(self, matrix: List[List[int]], threshold: int) -> int:
        n = len(matrix)
        m = len(matrix[0])
        mat = [[0] * (m + 1) for _ in range(n + 1)]

        for i in range(1, n + 1):
            for j in range(1, m + 1):
                mat[i][j] = matrix[i - 1][j - 1]
                if i > 0:
                    mat[i][j] += mat[i - 1][j]
                if j > 0:
                    mat[i][j] += mat[i][j - 1]
                if i > 0 and j > 0:
                    mat[i][j] -= mat[i - 1][j - 1]

        def check(i, j, l):
            if i + l - 1 > n:
                return False
            if j + l - 1 > m:
                return False
            return mat[i + l - 1][j + l - 1] - mat[i - 1][j + l - 1] - mat[i + l - 1][j - 1] + mat[i - 1][j - 1] <= threshold
        dp = [[0] * (m + 1) for _ in range(n + 1)]
        res = 0
        for i in range(1, n + 1):
            for j in range(1, m + 1):
                if matrix[i - 1][j - 1] > threshold:
                    dp[i][j] = 0
                    continue
                l = max(dp[i - 1][j - 1], dp[i - 1][j], dp[i][j - 1], 3) - 1
                while check(i, j, l):
                    l += 1
                dp[i][j] = l - 1
                res = max(res, dp[i][j])

        return res

/**
 * @param {number[][]} mat
 * @param {number} threshold
 * @return {number}
 */
var maxSideLength = function(mat, threshold) {
  const rl = mat.length;
  const cl = mat[0].length;
  for (let size = Math.min(rl, cl); size >= 1; size--) {
    for (let y = 0; y <= rl - size; y++) {
      for (let x = 0; x <= cl - size; x++) {
        if (check(x, y, size)) {
          return size;
        }
      }
    }
  }
  return 0;

  function check(x, y, size) {
    let sum = 0;
    for (let dy = y; dy < y + size; dy++) {
      for (let dx = x; dx < x + size; dx++) {
        sum += mat[dy][dx];
        if (sum > threshold) {
          return false;
        }
      }
    }
    return true;
  }
};

