class Solution:
    def distinctSequences(self, n: int) -> int:

        @functools.lru_cache(None)
        def solve(t=0, his=(17, 17)) :
            if t == n :
                return 1

            to_ret = 0
            for i in range(1, 7) :
                if not math.gcd(i, his[1]) == 1 :
                    continue
                if i == his[0] or i == his[1] :
