class Solution {
	private static int[][] MAP = { {}, { 2, 3, 4, 5, 6 }, { 1, 3, 5 }, { 1, 2, 4, 5 }, { 1, 3, 5 }, { 1, 2, 3, 4, 6 },
			{ 1, 5 }, { 1, 2, 3, 4, 5, 6 } };

	public int distinctSequences(int n) {
		int[][][] dp = new int[n + 1][8][8];
		for (int i = 0; i <= n; i++) {
			for (int j = 1; j <= 7; j++) {
				for (int k = 1; k <= 7; k++) {
					for (int l : MAP[k]) {
						if (l != j) {
							dp[i][j][k] = i > 0 ? (dp[i][j][k] + dp[i - 1][k][l]) % 1000000007 : 1;
						}
					}
				}
			}
		}
		return dp[n][7][7];
	}
}

