class Solution {

	// has tried same value	
	private Set<Integer> set = new HashSet<>();
	private Map<Integer , List<Integer>> map = new HashMap<>();
	private int[] dist;

    public int minJumps(int[] arr) {
    	int i , j , n = arr.length;
    	for (i = 0;i < n;i ++) {
    		map.putIfAbsent(arr[i] , new ArrayList<>());
    		map.get(arr[i]).add(i);    		
    	}
    	dist = new int[n];
    	Arrays.fill(dist , - 1);
    	dist[0] = 0;
    	Queue<Integer> queue = new LinkedList<>();
    	queue.add(0);
    	while (!queue.isEmpty()) {
    		int current = queue.poll();
    		if (current == n - 1) {
    			return dist[current];    			
    		}
    		// + 1
    		if (current + 1 < n && dist[current + 1] < 0) {
    			dist[current + 1] = dist[current] + 1;
    			queue.add(current + 1);
    		}
    		// - 1
    		if (current - 1 >= 0 && dist[current - 1] < 0) {
    			dist[current - 1] = dist[current] + 1;
    			queue.add(current - 1);
    		}
    		// same    		
    		if (!set.contains(arr[current])) {
    			set.add(arr[current]);
    			for (int index : map.get(arr[current])) {
    				if (dist[index] < 0) {
    					dist[index] = dist[current] + 1;
    					queue.add(index);    					
    				}
    			}
    		}
    	}
    	return - 1;
    }
}

class Solution:
    def minJumps(self, arr: List[int]) -> int:
        graph = collections.defaultdict(list)
        for i, val in enumerate(arr):
            graph[val].append(i)
        queue = collections.deque([0])
        costs = {0 : 0}
        nsteps = 0
        target = len(arr) - 1
        while queue:
            sz = len(queue)
            nsteps += 1
            for _ in range(sz):
                idx = queue.popleft()
                if idx == target:
                    return nsteps - 1
                if idx > 0 and (idx - 1) not in costs:
                    queue.append(idx - 1)
                    costs[idx - 1] = nsteps
                if idx + 1 not in costs:
                    queue.append(idx + 1)
                    costs[idx + 1] = nsteps
                val = arr[idx]
                while graph[val]:
                    nei = graph[val].pop()
                    if nei not in costs:
                        costs[nei] = nsteps
                        queue.append(nei)
        return -1

/**
