var minJumps = function(arr) {
    if (arr.length === 1) return 0;
    const n = arr.length;
    const step = new Array(n).fill(-1);
    step[0] = 0;
    const m = {};
    for (let i = 0; i < n; i++) {
        m[arr[i]] = m[arr[i]] || [];
        m[arr[i]].push(i);
    }
    const r = [ 0 ];
    let l = 0;
    while (l < r.length) {
        if (step[r[l] + 1] === -1) {
            step[r[l] + 1] = step[r[l]] + 1;
            r.push(r[l] + 1);
        }
        if (step[r[l] - 1] === -1) {
            step[r[l] - 1] = step[r[l]] + 1;
            r.push(r[l] - 1);
        }
        for (let i = 0; i < m[arr[r[l]]].length; i++) {
            if (step[m[arr[r[l]]][i]] === -1) {
                step[m[arr[r[l]]][i]] = step[r[l]] + 1;
                r.push(m[arr[r[l]]][i]);
            }
        }
        m[arr[r[l]]] = [];
        if (step[n - 1] > -1) return step[n - 1];
        l++;
    }
};

use std::collections::HashMap;
use std::collections::VecDeque;

impl Solution {
    pub fn min_jumps(a: Vec<i32>) -> i32 {
        let n = a.len();
        let mut m = HashMap::new();

        for i in 0..n {
            m.entry(a[i]).or_insert(vec![]).push(i);
        }

        let mut vis = vec![false; n];
        vis[0] = true;

        let mut qu = VecDeque::new();
        qu.push_back(0);

        let mut step = 0;
        while !qu.is_empty() {
            step += 1;
            let mut sz = qu.len();
            while sz > 0 {
                sz -= 1;

                let u = qu.pop_front().unwrap();
                if u == n-1 {
                    return step - 1;
                }
                if u > 0 && !vis[u-1] {
                    vis[u-1] = true;
                    qu.push_back(u-1);
                }
                if u+1 < n && !vis[u+1] {
                    vis[u+1] = true;
                    qu.push_back(u+1);
                }

                if let Some(vs) = m.get(&a[u]) {
                    for &v in vs {
                        if !vis[v] {
                            vis[v] = true;
                            qu.push_back(v);
                        }
                    }
                }

            }
        }
        step
    }
}

