				tie(xx, yy) = elist.front();
				elist.pop();

				sx[xx].erase(yy);
				sy[yy].erase(xx);
			}
		}

		ans = dp[n - 1][m - 1];
		if (ans == 1E9) ans = -1;
		return ans;
	}
};

