                    return e
                r[q[0][0]], c[q[0][1]] = max(r[q[0][0]], q[0][1] + 1), max(c[q[0][1]], q[0][0] + 1)
                while r[q[0][0]] < len(grid[0]) and r[q[0][0]] <= q[0][1] + grid[q[0][0]][q[0][1]]:
                    q.append([q[0][0], r[q[0][0]]])
