                q.popleft()
            e += 1
        return -1

class Solution {

	public int minimumVisitedCells(int[][] grid) {
		int[] row = new int[grid.length], col = new int[grid[0].length];
		for (ArrayDeque<int[]> deque = new ArrayDeque<>(List.of(new int[3])); !deque.isEmpty();) {
			int[] poll = deque.poll();
			if (poll[1] == grid.length - 1 && poll[2] == grid[0].length - 1) {
				return poll[0] + 1;
			}
			for (row[poll[1]] = Math.max(row[poll[1]], poll[2] + 1); row[poll[1]] <= Math.min(grid[0].length - 1,
					poll[2] + grid[poll[1]][poll[2]]); row[poll[1]]++) {
				deque.offer(new int[] { poll[0] + 1, poll[1], row[poll[1]] });
			}
			for (col[poll[2]] = Math.max(col[poll[2]], poll[1] + 1); col[poll[2]] <= Math.min(grid.length - 1,
					poll[1] + grid[poll[1]][poll[2]]); col[poll[2]]++) {
				deque.offer(new int[] { poll[0] + 1, col[poll[2]], poll[2] });
			}
		}
		return -1;
	}
}

/**
 * @param {number[][]} grid
 * @return {number}
 */
var minimumVisitedCells = function (grid) {
  let queue = new Queue();
  let m = grid.length;
  let n = grid[0].length;
  let seen = new Array(m).fill().map(() => new Array(n));
  queue.enqueue([0, 0,1]);
  while (queue.size() > 0) {
    let [r, c, d] = queue.dequeue();
    let val = grid[r][c];
    if (r === m - 1 && c === n - 1) return d;

    for (let i = r + 1; i <= r + val; i += 1) {
      if (i >= m || seen[i][c] !== undefined) continue;
      seen[i][c] = true;
      queue.enqueue([i, c, d + 1]);
      if (i === m - 1 && c === n - 1) return d + 1;
    }

    for (let j = c + 1; j <= c + val; j += 1) {
      if (j >= n || seen[r][j] !== undefined) continue;
      seen[r][j] = true;
      queue.enqueue([r, j, d + 1]);
      if (r === m - 1 && j === n - 1) return d + 1;
    }
  }
  return -1;
};

