class Solution:
    def isSubPath(self, head: ListNode, root: TreeNode, started=False) -> bool:
        if head is None:
            return True
        if root is None:
            return False
        if head.val != root.val:
            if started:
                return False
            return self.isSubPath(head, root.left, False) or self.isSubPath(head, root.right, False)

        if started:
            return self.isSubPath(head.next, root.left, True) or self.isSubPath(head.next, root.right, True)
        return self.isSubPath(head.next, root.left, True) or self.isSubPath(head.next, root.right, True) or self.isSubPath(head, root.left, False) or self.isSubPath(head, root.right, False)

/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) { val = x; }
 * }
 */
/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
class Solution {
    public boolean isSubPath(ListNode head, TreeNode root) {
        if (root == null)
            return false;
        boolean ans = false;
        if (root.val == head.val) {
            ans = ans || isph(head.next, root.left);
            ans = ans || isph(head.next, root.right);
        }
        if (ans)
            return true;
        ans = ans || isSubPath(head, root.left);
        ans = ans || isSubPath(head, root.right);
        return ans;
    }

    public boolean isph(ListNode head, TreeNode root) {
        if (head == null)
            return true;
        if (root == null)
            return false;
        boolean ans = false;
        if (root.val == head.val) {
            ans = ans || isph(head.next, root.left);
            ans = ans || isph(head.next, root.right);
        }
        return ans;
    }
}

/**
 * Definition for singly-linked list.
 * function ListNode(val) {
 *     this.val = val;
 *     this.next = null;
 * }
 */
/**
 * Definition for a binary tree node.
 * function TreeNode(val) {
 *     this.val = val;
 *     this.left = this.right = null;
 * }
 */
/**
 * @param {ListNode} head
 * @param {TreeNode} root
 * @return {boolean}
 */
var isSubPath = function(head, root) {
  const arr = [];
  while (head) {
    arr.push(head.val);
    head = head.next;
  }
  const starts = [];
  const dfs = (node) => {
    if (!node) return;
    if (node.val === arr[0]) starts.push(node);
    dfs(node.left);
    dfs(node.right);
  };
  dfs(root);
  for (const s of starts) {
    const queue = [[s, 0]];
    while (queue.length > 0) {
      const [node, index] = queue.shift();
      if (!node || node.val !== arr[index]) continue;
      if (index === arr.length - 1) return true;
      queue.push([node.left, index + 1]);
      queue.push([node.right, index + 1]);
    }
  }
  return false;
};

struct union_find {
    vector<int> parent;
    vector<int> size;
    vector<uint8_t> rank;

    union_find(int n = 0) {
        if (n > 0)
            init(n);
    }

    void init(int n) {
        parent.resize(n + 1);
        rank.resize(n + 1);
        size.resize(n + 1);

        for (int i = 0; i <= n; i++) {
            parent[i] = i;
            size[i] = 1;
            rank[i] = 0;
        }
    }

    int find(int x) {
        return x == parent[x] ? x : parent[x] = find(parent[x]);
    }

    bool unite(int x, int y) {
        x = find(x);
        y = find(y);

        if (x == y)
            return false;

        if (rank[x] < rank[y])
            swap(x, y);
        else if (rank[x] == rank[y])
            rank[x]++;

        parent[y] = x;
        size[x] += size[y];
        return true;
    }
};


