        }
        for(int v : A){
        	int vv = v;
        	while(vv > 1){
        		ds.union(v, lpf[vv]);
        		vv /= lpf[vv];
        	}
        }
        int ret = 0;
        for(int i = 1;i <= 100000;i++){
        	if(ds.upper[i] < 0){
        		ret = Math.max(ret, ds.w[i]);
        	}
        }
        return ret;
