    public int[] enumLowestPrimeFactors(int n) {
		int tot = 0;
		int[] lpf = new int[n + 1];
		int u = n + 32;
		double lu = Math.log(u);
		int[] primes = new int[(int) (u / lu + u / lu / lu * 1.5)];
		for (int i = 2; i <= n; i++)
			lpf[i] = i;
		for (int p = 2; p <= n; p++) {
			if (lpf[p] == p)
				primes[tot++] = p;
			int tmp;
			for (int i = 0; i < tot && primes[i] <= lpf[p] && (tmp = primes[i] * p) <= n; i++) {
				lpf[tmp] = primes[i];
			}
		}
		return lpf;
	}


    public class DJSet {
		public int[] upper;
		public int[] w;

		public DJSet(int n) {
			upper = new int[n];
			Arrays.fill(upper, -1);
			w = new int[n];
		}

		public int root(int x) {
			return upper[x] < 0 ? x : (upper[x] = root(upper[x]));
		}

		public boolean equiv(int x, int y) {
			return root(x) == root(y);
		}

		public boolean union(int x, int y) {
			x = root(x);
			y = root(y);
			if (x != y) {
				if (upper[y] < upper[x]) {
					int d = x;
					x = y;
					y = d;
				}
				upper[x] += upper[y];
				upper[y] = x;
				w[x] += w[y];
			}
			return x == y;
		}

		public int count() {
			int ct = 0;
			for (int u : upper)
				if (u < 0)
					ct++;
			return ct;
		}
	}

}

class UF(object):
    def __init__(self, nodes):
        self.idx = {v:i for i,v in enumerate(nodes)}
        self.parent = range(len(nodes))
        self.rank = [0] * len(nodes)
        self.size = [1] * len(nodes)
    def union(self, node1, node2):
        i1, i2 = self._find(self.idx[node1]), self._find(self.idx[node2])
        if i1 == i2: return
        if self.rank[i1] < self.rank[i2]: i1, i2 = i2, i1
        # self.rank[i1] >= self.rank[i2]
        self.parent[i2] = i1
        self.size[i1] += self.size[i2]
        if self.rank[i1] == self.rank[i2]:
            self.rank[i1] += 1
    def groupsize(self, node):
        return self.size[self.idx[node]]
    def _find(self, i):
        path = []
        while self.parent[i] != i:
            path.append(i)
            i = self.parent[i]
        for j in path: self.parent[j] = i
        return i



class Solution(object):
    def largestComponentSize(self, A):
        """
        :type A: List[int]
        :rtype: int
        """
        m = max(A)
        primes = [2]
        def is_prime(x):
            for p in primes:
                if p * p > x: return True
                if x % p == 0: return False
        for x in xrange(3, m+1, 2):
            if is_prime(x):
                primes.append(x)
            if x * x > m: break
        def prime_factors(x):
            r = []
            for p in primes:
                if p * p > x: break
                if x % p == 0:
                    r.append(p)
                    while x % p == 0: x //= p
            if x > 1: r.append(x)
            return r
        d = {}
        uf = UF(A)
        for v in A:
            for p in prime_factors(v):
                d.setdefault(p, []).append(v)
        for vv in d.itervalues():
            for i in xrange(len(vv)-1):
                uf.union(vv[i], vv[i+1])
        return max(uf.groupsize(node) for node in A)

class Solution:
    def largestComponentSize(self, A):
        """
        :type A: List[int]
        :rtype: int
        """
        A.sort()
        mx = max(A)
        DSU = [i for i in range(mx + 1)]
        #b = set(A)
        b = [False] * (mx + 1)
        for a in A:
            b[a] = True

        def find(x):
            if DSU[x] != x:
                DSU[x] = find(DSU[x])
            return DSU[x]

        def union(x, y):
            DSU[find(x)] = find(y)

        l = []
        cc = [False] * (mx + 1)
        for x in range(2, mx):
            if cc[x]: continue
            l.clear()
            n = x
            while n <= mx:
                cc[n] = True
                if b[n]:
                #if n in b:
                    l.append(n)
                n += x
            if len(l) > 1:
                t = find(l[0])
                for j in range(1, len(l)):
                    DSU[find(l[j])] = t

        c = collections.defaultdict(int)

        for a in A:
            c[find(a)] += 1
        return max(c.values())

/**
