var largestComponentSize = function(A) {
  const UnionFind = function() {this.father_ = {};};
  UnionFind.prototype.getFather = function(x) {
    if (this.father_[x] == null) return x;
    return this.father_[x] = this.getFather(this.father_[x]);
  };
  UnionFind.prototype.union = function(x, y) {
    if (this.getFather(x) === this.getFather(y)) return;
    this.father_[this.getFather(x)] = this.getFather(y);
  };

  const buckets = {};
  for (let i of A) {
    for (let j of factors[i]) {
      buckets[j] = buckets[j] || [];
      buckets[j].push(i);
    }
  }
  const unionFind = new UnionFind();
  for (let list of Object.values(buckets)) {
    for (let i = 1; i < list.length; i++) unionFind.union(list[i], list[i - 1]);
  }
  const sizes = {};
  for (let i of A) {
    sizes[unionFind.getFather(i)] = (sizes[unionFind.getFather(i)] || 0) + 1;
  }
  return Object.values(sizes).reduce((prev, cur) => Math.max(prev, cur), 0);
};

