class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
        multiset<ll> s = {0};
        set<pair<ll, ll>> t;
        vector<ll> psa = {0};
        for (int i : nums) psa.push_back(psa.back()+i);
        ll cur = 0;
        for (int i : nums) cur += i;
        s.insert(cur);
        t.insert(make_pair(nums.size()-1, 0));
        vector<ll> res;
        for (int i : removeQueries) {
            auto cur = *t.lower_bound(make_pair(i, 0));
            t.erase(cur);
            int l = cur.second, r = cur.first;
            s.erase(s.find(psa[r+1]-psa[l]));
            if (i != l) {
                t.insert(make_pair(i-1, l));
                s.insert(psa[i]-psa[l]);
            }
            if (i != r) {
                t.insert(make_pair(r, i+1));
                s.insert(psa[r+1]-psa[i+1]);
            }
            res.push_back(*s.rbegin());
        }
        return res;
    }
};

from sortedcontainers import SortedDict

class Solution(object):
    def maximumSegmentSum(self, nums, removeQueries):
        """
        :type nums: List[int]
        :type removeQueries: List[int]
        :rtype: List[int]
        """
        r, p, s, t = [0] * len(nums), [0] * len(nums), 0, SortedDict({0: len(nums) - 1})
        for i in range(len(nums)):
            s, p[i] = s + nums[i], s + nums[i]
        u, i = SortedDict({s: 1}), 0
        for q in removeQueries:
            l, v = t.keys()[t.bisect_left(q + .1) - 1], t[t.keys()[t.bisect_left(q + .1) - 1]]
            t.pop(l)
            if u[p[v] - (p[l - 1] if l > 0 else 0)] == 1:
                u.pop(p[v] - (p[l - 1] if l > 0 else 0))
            else:
                u[p[v] - (p[l - 1] if l > 0 else 0)] = u[p[v] - (p[l - 1] if l > 0 else 0)] - 1
            if q > l:
                u[p[q - 1] - (p[l - 1] if l > 0 else 0)], t[l] = (u[p[q - 1] - (p[l - 1] if l > 0 else 0)] if p[q - 1] - (p[l - 1] if l > 0 else 0) in u else 0) + 1, q - 1
            if q < v:
                u[p[v] - p[q]], t[q + 1] = (u[p[v] - p[q]] if p[v] - p[q] in u else 0) + 1, v
            r[i], i = u.keys()[-1] if u else 0, i + 1
        return r

class Solution {
	public long[] maximumSegmentSum(int[] nums, int[] removeQueries) {
		int n = nums.length;
		DJSet ds = new DJSet(n);
		long[] ret = new long[removeQueries.length];
		boolean[] alive = new boolean[nums.length];
		long max = 0;
		for(int i = removeQueries.length-1;i >= 1;i--){
			int t = removeQueries[i];
			alive[t] = true;
			ds.sum[t] += nums[t];
			if(t-1 >= 0 && alive[t-1]){
				ds.unite(t, t-1);
			}
			if(t+1 < n && alive[t+1]){
				ds.unite(t, t+1);
			}
			max = Math.max(max, ds.sum[ds.root(t)]);
			ret[i-1] = max;
		}
		return ret;
	}

	public class DJSet {
		public int[] upper;
		public long[] sum;

		public DJSet(int n) {
			upper = new int[n];
			sum = new long[n];
			Arrays.fill(upper, -1);
		}

		public int root(int x) {
			return upper[x] < 0 ? x : (upper[x] = root(upper[x]));
		}

		public boolean equiv(int x, int y) {
			return root(x) == root(y);
		}

		public boolean unite(int x, int y) {
			x = root(x);
			y = root(y);
			if (x != y) {
				if (upper[y] < upper[x]) {
					int d = x;
					x = y;
					y = d;
				}
				upper[x] += upper[y];
				upper[y] = x;
				sum[x] += sum[y];
			}
			return x == y;
		}

		public int count() {
			int ct = 0;
			for (int u : upper) if (u < 0) ct++;
			return ct;
		}

		public int[][] toBucket() {
			int n = upper.length;
			int[][] ret = new int[n][];
			int[] rp = new int[n];
			for (int i = 0; i < n; i++) if (upper[i] < 0) ret[i] = new int[-upper[i]];
			for (int i = 0; i < n; i++) {
				int r = root(i);
				ret[r][rp[r]++] = i;
			}
			return ret;
		}
	}

}

use std::collections::{BTreeSet, BTreeMap};

impl Solution {
    pub fn maximum_segment_sum(nums: Vec<i32>, remove_queries: Vec<i32>) -> Vec<i64> {
        let nums = nums.into_iter().map(|x| x as i64).collect::<Vec<_>>();
        let n = nums.len();
        let mut ret = Vec::with_capacity(n);
        let mut presum = nums.clone();
        for i in 1..n {
            presum[i] += presum[i - 1];
        }
        let sum = |l: usize, r: usize| {
            presum[r] - if l == 0 { 0 } else { presum[l - 1] }
        };
        let mut counter = Counter::default();
        counter.insert(presum[n - 1]);
        let mut indices = BTreeSet::new();
        indices.insert(-1);
        indices.insert(n as i32);
        for idx in remove_queries {
            let left = *indices.range(..idx).next_back().unwrap() + 1;
            let right = *indices.range(idx..).next().unwrap() - 1;
            indices.insert(idx);

            let (left, right, idx) = (left as usize, right as usize, idx as usize);
            let sum_range = sum(left, right);
            counter.remove(sum_range);
            if idx > left {
                let suml = sum(left, idx - 1);
                counter.insert(suml);
            }
            if idx < right {
                let sumr = sum(idx + 1, right);
                counter.insert(sumr);
            }
            let ans = *counter.0.keys().rev().next().unwrap_or(&0);
            ret.push(ans);
        }

        ret
    }
}

#[derive(Default)]
struct Counter(BTreeMap<i64, usize>);

impl Counter {
    pub fn insert(&mut self, x: i64) {
        *self.0.entry(x).or_default() += 1;
    }

    pub fn remove(&mut self, x: i64) {
        if {
            match self.0.get_mut(&x) {
                Some(cnt) => {
                    *cnt -= 1;
                    *cnt == 0
                }
                None => false,
            }
        } {
            self.0.remove(&x);
        }
    }
}

