class Solution {
public:
    int maxDistance(vector<int>& position, int m) {
        sort(all(position));
        int lhs = 1;
        int rhs = 1e9;
        while(lhs < rhs) {
          int mid = (lhs+rhs+1)/2;
          int last = -1e9;
          int placed = 0;
          for(int out: position) {
            if(out - last >= mid) {
              placed++;
              last = out;
            }
          }
          if(placed >= m) lhs = mid;
          else rhs = mid-1;
        }
        return lhs;
    }
};

class Solution {
    public int maxDistance(int[] a, int m) {
    	Arrays.sort(a);
        int low = 0, high = 1000000007;
        while(high - low > 1){
        	int h = high+low>>1;
        	if(ok(a, h, m)){
        		low = h;
        	}else{
        		high = h;
        	}
        }
        return low;
    }

    boolean ok(int[] a, int h, int m)
    {
    	int r = -1;
    	int u = 0;
    	for(int v : a){
    		if(r <= v){
    			r = v + h;
    			u++;
    		}
    	}
    	return u >= m;
    }
}

class Solution:
    def maxDistance(self, position: List[int], m: int) -> int:
        position.sort()

        head = 0
        tail = position[-1]

        def good(x):
            start = -1e100

            count = 0
            for p in position:
                if p - start >= x:
                    start = p
                    count += 1
            return count >= m

        while head < tail:
            mid = (head + tail) // 2

            if good(mid + 1):
                head = mid + 1
            else:
                tail = mid

        return head

class Solution(object):
    def maxDistance(self, position, m):
        """
        :type position: List[int]
        :type m: int
        :rtype: int
        """
        a = sorted(position)
        n = len(a)
        def _test(d):
            prev = float('-inf')
            x = 0
            for i in a:
                if i - prev >= d:
                    x += 1
                    prev = i
                    if x >= m:
                        return True
            return False

        x, y = 1, max(a)
        while x + 1 < y:
            z = (x + y) >> 1
            if _test(z):
                x = z
            else:
                y = z
        return x

/**
