var maxDistance = function(position, m) {
    position = [...position].sort((a, b) => a-b);
    const n = position.length;

    let lo = 1;
    let hi = (position[n-1] - position[0]) / (m-1);

    function f(force) {
        const res = [];
        let prev;
        for(const x of position){
            if(!prev || x-prev >= force){
                res.push(x);
                prev = x;
            }
            if(res.length >= m)
                return true;
        }
        return false; 
    }

    let mid;
    while(lo < hi) {
        mid = Math.ceil((lo+hi)/2);
        if(f(mid))
            lo = mid;
        else
            hi = mid - 1;
    }
    return lo;
};

