var maxFreq = function(s, maxLetters, minSize, maxSize) {
    let maxOccurrences = 0;
    const map = new Map();

    for (let i = minSize; i <= s.length; ++i) {
        for (let l = minSize; l <= maxSize; ++l) {
            if (i < l) continue;

            const substring = s.substring(i - l, i);
            const chars = new Set();

            for (let j = 0; j < substring.length; ++j) {
                chars.add(substring[j]);
            }

            if (chars.size > maxLetters) continue;

            // console.log(i, l, substring);

            map.set(substring, (map.get(substring) || 0) + 1);

            maxOccurrences = Math.max(maxOccurrences, map.get(substring));
        }
    }

    // console.log(map);

    return maxOccurrences;
}

