      if (r->cs[c]==NULL) r->cs[c] = alloc();
      r = r->cs[c];
      if (cs[c]==0) { cs[c]=1; ccc++;}
      if (ccc>maxLetters) break;
    }
    if (j<minSize-1) continue;
    for (; j<maxSize; j++) {
      if ((i+j) >= n) break;
      c = s[i+j]-'a';
      if (cs[c]==0) { cs[c]=1; ccc++;}
      if (ccc>maxLetters) break;
      if (r->cs[c]==NULL) r->cs[c] = alloc();
      r = r->cs[c];
      r->c++;
      if (rc<r->c) rc = r->c;
    }
  }
  return rc;
}

