	class Solution {
	    public List<String> maxNumOfSubstrings(String s_) {
	        char[] s = s_.toCharArray();
	        int n = s.length;
	        int[][] flc = new int[26][4];
	        for(int i = 0;i < 26;i++){
	        	Arrays.fill(flc[i], -1);
	        	flc[i][2] = 0;
	        	flc[i][3] = i;
	        }
	        for(int i = 0;i < n;i++){
	        	int c = s[i]-'a';
	        	if(flc[c][0] == -1)flc[c][0] = i;
	        	flc[c][1] = i;
	        	flc[c][2]++;
	        }
	        Arrays.sort(flc, (x, y) -> x[0] - y[0]);
	        int[] reach = new int[26];
	        int[] lens = new int[26];
	        Arrays.fill(reach, -1);
	        for(int i = 0;i < 26;i++){
	        	if(flc[i][0] == -1)continue;
        		int max = flc[i][1];
        		int len = flc[i][2];
    			if(max - flc[i][0] + 1 == len){
    				reach[i] = i;
    				lens[i] = len;
    				continue;
    			}
        		for(int j = i+1;j < 26;j++){
        			max = Math.max(max, flc[j][1]);
        			len += flc[j][2];
        			if(max - flc[i][0] + 1 == len){
        				reach[i] = j;
        				lens[i] = len;
        				break;
        			}
        		}
	        }
	        int[] dp = new int[27];
	        int[] lsum = new int[27];
	        int[] prev = new int[27];
	        dp[0] = 0;
	        for(int i = 1;i <= 26;i++){
	        	dp[i] = dp[i-1];
	        	lsum[i] = lsum[i-1];
	        	prev[i] = -1;

	        	for(int j = 0;j < i;j++){
	        		if(reach[j] == i-1){
	        			int ndp = dp[j] + 1;
	        			int nsum = lsum[j] + lens[j];
	        			if(ndp > dp[i] || ndp == dp[i] && nsum < lsum[i]){
	        				dp[i] = ndp;
	        				lsum[i] = nsum;
	        				prev[i] = j;
	        			}
	        		}
	        	}
	        }

	        int arg = 0;
	        for(int i = 0;i <= 26;i++){
	        	if(dp[i] > dp[arg] || dp[i] == dp[arg] && lsum[i] < lsum[arg]){
	        		arg = i;
	        	}
	        }
	        List<String> ret = new ArrayList<>();
	        int cur = arg;
	        while(cur > 0){
	        	int p = prev[cur];
	        	if(p == -1){
	        		cur--;
	        		continue;
	        	}
	        	ret.add(new String(s, flc[p][0], lens[p]));
	        	cur = p;
	        }
	        return ret;
	    }
	}

