            for i in range(st,ed+1):
                visible.add(s[i])
            lookup[c] = visible
        for c in first:
            seen = set()
            def dfs(cur,seen):
                seen.add(cur)
                for n in lookup[cur]:
                    if n not in seen:
                        dfs(n,seen)
            dfs(c,seen)
            lower = min(first[c] for c in seen)
            upper = max(last[c] for c in seen)
            intervals.add((lower, upper))
        pq = []
        intervals = list(intervals)
        intervals.sort(key = lambda x : x[1] - x[0])
        ans = []
        taken = set()
        for a,b in intervals:
