from itertools import count
class Solution(object):
    def maxNumOfSubstrings(self, s):
        """
        :type s: str
        :rtype: List[str]
        """
        first = {}
        last = {}
        for i, c in enumerate(s):
            if c not in first: first[c] = i
            last[c] = i
        best = [-1]
        r = []
        dep = {}
        depth = count()
        def dfs(i):
            p = i
            c = s[i]
            low = dep[c] = next(depth)
            j = last[c]
            while i <= j:
                if s[i] == c:
                    i += 1
                    continue
                if s[i] in dep:
                    low = min(low, dep[s[i]])
                    i += 1
                    continue
                i, t = dfs(i)
                low = min(low, t)
                j = max(j, i)
                i += 1
            if low >= dep[c] and p > best[0]:
                r.append(s[p:j+1])
                best[0] = j
            return j, low
        for i, c in enumerate(s):
            if c not in dep: dfs(i)
        return r

use std::collections::*;

impl Solution {
    pub fn max_num_of_substrings(s: String) -> Vec<String> {
        let mut start = BTreeMap::new();
        let mut end = BTreeMap::new();
        let cs = s.chars().collect::<Vec<_>>();
        let n = cs.len();
        for i in 0..n {
            let c = cs[i];
            start.entry(c).or_insert(i);
            end.insert(c, i);
        }
        //println!("{:?} {:?}", start, end);
        let mut seg_set = BTreeSet::new();
        for i in 0..26 {
            let c = ('a' as u8 + i) as char;
            if !start.contains_key(&c) {
                continue;
            }
            let mut r = end[&c];
            let mut l = start[&c];
            let mut old_r = r;
            let mut old_l = l;
            loop {
                for p in l..=r {
                    l = l.min(start[&cs[p]]);
                    r = r.max(end[&cs[p]]);
                }
                if l == old_l && r == old_r {
                    break;
                }
                old_l = l;
                old_r = r;
            }
            seg_set.insert((l, r));
        }
        let seg = seg_set.into_iter().collect::<Vec<_>>();
        //println!("{:?}", seg);


        let mut dp = vec![];
        dp.push((1, seg[0].1 - seg[0].0 + 1, vec![0]));
        let mut ans = dp[0].clone();
        for i in 1..seg.len() {
            let mut count = 1;
            let mut len = seg[i].1 - seg[i].0 + 1;
            let mut list = vec![i];
            for j in 0..i {
                if seg[j].1 >= seg[i].0 {
                    continue;
                }
                let new_count = dp[j].0 + 1;
                let new_len = dp[j].1 + seg[i].1 - seg[i].0 + 1;
                if new_count > count || (new_count == count && new_len < len) {
                    count = new_count;
                    len = new_len;
                    list = dp[j].2.clone();
                    list.push(i);
                }
            }
            let new_item = (count, len, list);
            dp.push(new_item.clone());
            if ans.0 < count || (ans.0 == count && len < ans.1) {
                ans = new_item;
            }
        }
        //println!("{:?}", dp);
        //println!("{:?}", ans);
        let mut ans_arr = vec![];
        for p in ans.2 {
            ans_arr.push(cs[seg[p].0..=seg[p].1].iter().copied().collect::<String>());
        }
        ans_arr
    }
}

