var maxNumOfSubstrings = function(s) {
    let index = {}
    for (let i = 0; i < 26; i++) {
        let letter = String.fromCharCode(97 + i)
        let start = s.indexOf(letter)
        let end = start === -1 ? -1 : s.lastIndexOf(letter)
        index[letter] = {
            start,
            end
        }
    }

    let strs = []
    for (let i = 0; i < s.length; i++) {
        let end = index[s[i]].end
        for (let j = i; j < s.length; j++) {
            let tarIndex = index[s[j]]
            if (tarIndex.start < i) {
                break
            } else if (tarIndex.end > end) {
                end = tarIndex.end
            } else if (j === end) {
                strs.push([i, j])
                break
            }
        }
    }

    let ret = []
    for (let i = 0; i < strs.length; i++) {
        let str = strs[i]
        let strN = strs[i + 1]
        if (!strN || strN[0] > str[1]) {
            ret.push(s.substring(str[0], str[1] + 1))
        }
    }
    return ret
};

