public class Solution {

    List<Integer> value;
    List<Integer> length;
    int[][][] f;

    int sqr(int i) {
        return i * i;
    }

    int F(int i, int j, int k) {
        if (i == j) return sqr(length.get(i) + k);

        if (f[i][j][k] != -1) return f[i][j][k];
        int ans = 0;
        ans = F(i, j - 1, 0) + sqr(length.get(j) + k);
        for (int l = i; l < j; l++) {
            if (value.get(l) == value.get(j)) {
                ans = Math.max(ans, F(i, l, length.get(j) + k) + F(l + 1, j - 1, 0));
            }
        }

        f[i][j][k] = ans;
        return ans;
    }

    public int removeBoxes(int[] boxes) {
        if (boxes == null || boxes.length == 0) return 0;

        value = new ArrayList<>();
        length = new ArrayList<>();
        for (int i = 0, j; i < boxes.length; ) {
            for (j = i + 1; j < boxes.length && boxes[i] == boxes[j]; j++) ;
            value.add(boxes[i]);
            length.add(j - i);
            i = j;
        }

//        System.out.println(value);
//        System.out.println(length);

        int m = value.size();
        f = new int[m + 10][m + 10][boxes.length + 10];
        for (int i = 0; i < f.length; i++)
            for (int j = 0; j < f[i].length; j++)
                for (int k = 0; k < f[i][j].length; k++)
                    f[i][j][k] = -1;

        return F(0, m - 1, 0);
    }

