            for (int i = head[top.n]; i; i = e[i].next) {
                int node = e[i].node;
                if (d[node] > top.d + e[i].w) {
                    d[node] = top.d + e[i].w;
                    q.push({node, d[node]});
                }
            }
        }
        return d[T];
    }

    int minCost(vector<vector<int>>& grid) {
        n = grid.size(), m = grid[0].size();
        fill(head + 1, head + n * m + 1, 0);
        tot = 0;
        for (int i = 1; i <= n; ++i)
            for (int j = 1; j <= m; ++j) {
                int cur_d = grid[i - 1][j - 1] - 1;
                for (int d = 0; d < 4; ++d) {
                    int nx = i + dir[d][0], ny = j + dir[d][1];
                    if (!valid(nx, ny)) continue;
                    int w = (d == cur_d) ? 0 : 1;
                    addedge(node(i, j), node(nx, ny), w);
                }
            }

        int w = dijkstra(node(1, 1), node(n, m));
        return w;
    }
};

class Solution:
    def minCost(self, grid: List[List[int]]) -> int:
        directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]

        rows = len(grid)
        cols = len(grid[0])

        pq = []
        heapq.heappush(pq, (0, 0, 0))
        best = {}
        best[(0, 0)] = 0

        while len(pq) > 0:
            d, x, y = heapq.heappop(pq)

            if x == rows - 1 and y == cols - 1:
                return d

            for i, (dx, dy) in enumerate(directions):
                nx, ny = x + dx, y + dy
                if 0 <= nx < rows and 0 <= ny < cols:
                    cost = 1
                    if i + 1 == grid[x][y]:
                        cost = 0

                    newd = d + cost
                    if (nx, ny) not in best or best[(nx, ny)] > newd:
                        heapq.heappush(pq, (newd, nx, ny))
                        best[(nx, ny)] = newd
        return 0

class Solution {
    public int minCost(int[][] grid) {
        ArrayList<ArrayList<Integer>> g0 = new ArrayList<>();
        ArrayList<ArrayList<Integer>> g1 = new ArrayList<>();
        int n = grid.length;
        int m = grid[0].length;
        int nm = n*m;
        for (int i = 0; i < nm; ++i) {
            g0.add(new ArrayList<>());
            g1.add(new ArrayList<>());
        }
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                int u = i*m+j;
                if (j<m-1) {
                    if (grid[i][j]==1)
                        g0.get(u).add(u+1);
                    else
                        g1.get(u).add(u+1);
                }
                if (j>0) {
                    if (grid[i][j]==2)
                        g0.get(u).add(u-1);
                    else
                        g1.get(u).add(u-1);
                }
                if (i>0) {
                    if (grid[i][j]==4)
                        g0.get(u).add(u-m);
                    else
                        g1.get(u).add(u-m);
                }
                if (i<n-1) {
                    if (grid[i][j]==3)
                        g0.get(u).add(u+m);
                    else
                        g1.get(u).add(u+m);
                }
            }
        }
        int[] dist = new int[nm];
        for (int i = 1; i < nm; ++i)
            dist[i] = 99999;
        ArrayDeque<Integer> q = new ArrayDeque<Integer>();
        q.add(0);
        while (!q.isEmpty()) {
            int u = q.removeFirst();
            int d = dist[u];
            for (int v : g0.get(u)) {
                if (d < dist[v]) {
                    dist[v] = d;
                    q.addFirst(v);
                }
            }
            for (int v : g1.get(u)) {
                if (d+1 < dist[v]) {
                    dist[v] = d+1;
                    q.add(v);
                }
            }
        }
        return dist[nm-1];
    }
}

class Solution(object):
