var minCost = function(grid) {
  const h = grid.length;
  const w = grid[0].length;
  const costs = [];
  for (let i = 0; i < h; i++) {
    costs[i] = [];
    for (let j = 0; j < w; j++) {
      costs[i][j] = Infinity;
      grid[i][j]--;
    }
  }
  costs[0][0] = 0;
  const dirs = [[0, 1], [0, -1], [1, 0], [-1, 0]];
  const queue = [[0, 0]];
  while (queue.length > 0) {
    const [i, j] = queue.shift();
    for (let d = 0; d < 4; d++) {
      const cost = d === grid[i][j] ? 0 : 1;
      const [ai, aj] = [i + dirs[d][0], j + dirs[d][1]];
      if (ai < 0 || ai >= h || aj < 0 || aj >= w) continue;
      if (costs[ai][aj] <= costs[i][j] + cost) continue;
      costs[ai][aj] = costs[i][j] + cost;
      queue.push([ai, aj]);
    }
  }
  return costs[h - 1][w - 1];
};

