class CombinationIterator {
	char[] u;
	int K;
	int ind;
	int[][] a;

    public CombinationIterator(String characters, int combinationLength) {
        u = characters.toCharArray();
        K = combinationLength;

        int n = u.length;
        a = new int[1<<n][];
        int p = 0;
        for(int i = 0;i < 1<<n;i++){
        	if(Integer.bitCount(i) == K){
        		int[] row = new int[K];
        		int t = i;
        		for(int j = 0;j < K;j++){
        			row[j] = Integer.numberOfTrailingZeros(t);
        			t &= t-1;
        		}
        		a[p++] = row;
        	}
        }
        a = Arrays.copyOf(a, p);
        Arrays.sort(a, new Comparator<int[]>() {
			public int compare(int[] a, int[] b) {
				for(int i = 0;i < a.length;i++){
					if(a[i] != b[i]){
						return a[i] - b[i];
					}
				}
				return 0;
			}
		});

        ind = -1;

    }

    public String next() {
    	ind++;
    	char[] ret = new char[K];
    	int p = 0;
    	for(int o : a[ind]){
    		ret[p++] = u[o];
    	}
    	return new String(ret);
    }

    public boolean hasNext() {
    	return ind+1 < a.length;
    }
}

class CombinationIterator(object):

    def __init__(self, characters, combinationLength):
        """
        :type characters: str
        :type combinationLength: int
        """
        self.s = characters
        self.k = combinationLength
        self.it = itertools.combinations(self.s, self.k)
        self.todo = self.binom(len(self.s), self.k)

    def binom(self, n, k):
        r = 1
        for i in xrange(1, k+1):
            r *= n-i+1
            r /= i
        return r


    def next(self):
        self.todo -= 1
        return "".join(next(self.it)) 

    def hasNext(self):
        return bool(self.todo)



