function generator(characters, length) {
  const res = [];
  const dfs = (startIndex, path) => {
    if (path.length === length) {
      res.push(path.join(""));
      return;
    }
    for (let i = startIndex; i < characters.length; i++) {
      path.push(characters[i]);
      dfs(i + 1, path);
      path.pop();
    }
  };
  dfs(0, []);
  return res;
}

