  return this.res[this.idx++];
};

/**
 * @return {boolean}
 */
CombinationIterator.prototype.hasNext = function() {
  return this.idx < this.res.length;
};

class Solution {
 public:
  int rootCount(vector<vector<int>>& edges, vector<vector<int>>& guesses, int k) {
    int n = edges.size() + 1;
    vector<vector<int>> a(n);
    for (auto& v : edges) {
      int x = v[0], y = v[1];
      a[x].push_back(y);
      a[y].push_back(x);
    }
    vector<int> tin(n), tout(n), dep(n);
    int ts = 0;
    function<void(int, int)> dfs = [&](int u, int parent) {
      tin[u] = ts++;
      dep[u] = parent < 0 ? 0 : dep[parent] + 1;
      for (auto& v : a[u]) {
        if (v == parent) continue;
        dfs(v, u);
      }
      tout[u] = ts;
    };
    dfs(0, -1);

    vector<int> cnt(n + 1);
    auto add = [&](int L, int R) {
      cnt[L]++;
      cnt[R]--;
    };
    for (auto& v : guesses) {
      int x = v[0], y = v[1];
      if (dep[x] < dep[y]) {
        add(0, tin[y]);
        add(tout[y], n);
      } else {
        add(tin[x], tout[x]);
      }
    }
    for (int i = 1; i < n; ++i) cnt[i] += cnt[i - 1];
    int ret = 0;
    for (int i = 0; i < n; ++i) {
      ret += cnt[i] >= k;
    }
    return ret;

  }
};

class Solution {

	public int rootCount(int[][] edges, int[][] guesses, int k) {
		HashMap<Integer, ArrayList<Integer>> graph = new HashMap<>();
		HashMap<List<Integer>, Integer> map = new HashMap<>();
		for (int[] edge : edges) {
			graph.computeIfAbsent(edge[0], t -> new ArrayList<>()).add(edge[1]);
			graph.computeIfAbsent(edge[1], t -> new ArrayList<>()).add(edge[0]);
		}
		for (int[] guess : guesses) {
			map.put(List.of(guess[0], guess[1]), map.getOrDefault(List.of(guess[0], guess[1]), 0) + 1);
		}
		return rootCount(0, -1, rootCount(0, -1, graph, map), k, graph, map);
	}

	private int rootCount(int u, int from, HashMap<Integer, ArrayList<Integer>> graph,
			HashMap<List<Integer>, Integer> map) {
		int count = 0;
		for (int v : graph.get(u)) {
			count += v == from ? 0 : rootCount(v, u, graph, map) + map.getOrDefault(List.of(u, v), 0);
		}
		return count;
	}

	private int rootCount(int u, int from, int count, int k, HashMap<Integer, ArrayList<Integer>> graph,
			HashMap<List<Integer>, Integer> map) {
		int result = count < k ? 0 : 1;
		for (int v : graph.get(u)) {
			result += v == from ? 0
					: rootCount(v, u, count - map.getOrDefault(List.of(u, v), 0) + map.getOrDefault(List.of(v, u), 0),
							k, graph, map);
		}
		return result;
	}
}

/**
 * @param {number[][]} edges
 * @param {number[][]} guesses
 * @param {number} k
 * @return {number}
 */
var rootCount = function(edges, guesses, k) {
    let n = edges.length + 1;
    let a = new Array(n).fill(0).map(_ => new Array());
    let b = new Array(n).fill(0).map(_ => new Array());
    let c = new Array(n).fill(0).map(_ => new Set());
    for (let [x, y] of edges)
    {
        a[x].push(y);
        a[y].push(x);
    }
    for (let [x, y] of guesses)
    {
        b[x].push(y);
        c[x].add(y);
    }
    let ans = 0, Ans = 0;
    const DFS = (x, y) =>
    {
        let res = new Set();
        for (let v of a[x])
            if (v != y) res.add(v);
        for (let v of b[x])
            if (res.has(v))
                ans ++;
        for (let v of a[x])
            if (v != y)
                DFS(v, x);
    }
    DFS(0, -1);
    const DFS2 = (x, y, ans) =>
    {
        if (ans >= k)
            Ans ++;
        for (let v of a[x])
            if (v != y)
            {
                let cur = ans;
                if (c[x].has(v))
                    cur --;
                if (c[v].has(x))
                    cur ++;
                DFS2(v, x, cur);
            }
    }
    DFS2(0, -1, ans);
    return Ans;
};

impl Solution {
    pub fn root_count(edges: Vec<Vec<i32>>, guesses: Vec<Vec<i32>>, k: i32) -> i32 {
        use std::collections::HashMap;

        let n = edges.len() + 1;
        let mut adj = vec![vec![]; n];

        for e in edges.iter() {
            let (a, b) = (e[0] as usize, e[1] as usize);
            adj[a].push(b);
            adj[b].push(a);
        }

        let mut gs = HashMap::new();
        for g in guesses.iter() {
            let (u, v) = (g[0] as usize, g[1] as usize);
            *gs.entry((u, v)).or_insert(0) += 1;
        }

        fn dfs(adj: &[Vec<usize>], curr: usize, from: usize, gs: &HashMap<(usize, usize), usize>) -> usize {
            let mut ret = 0;
            for &next in adj[curr].iter() {
                if next == from {
                    continue;
                }
                ret += *gs.get(&(curr, next)).unwrap_or(&0);
                ret += dfs(adj, next, curr, gs);
            }
            ret
        }

        let cnt0 = dfs(&adj, 0, 0, &gs);
        let k = k as usize;

        fn dfs2(adj: &[Vec<usize>], curr: usize, from: usize, gs: &HashMap<(usize, usize), usize>, k: usize, cnt: usize, ret: &mut i32) {
            if cnt >= k {
                *ret += 1;
            }
            for &next in adj[curr].iter() {
                if next == from {
                    continue;
                }
                let delta = *gs.get(&(next, curr)).unwrap_or(&0) - *gs.get(&(curr, next)).unwrap_or(&0);
                dfs2(adj, next, curr, gs, k, cnt + delta, ret);
            }
        }

        let mut ret = 0;
        dfs2(&adj, 0, 0, &gs, k, cnt0, &mut ret);
        ret
    }
}

class Solution {
public:
    int next_index(vector<bool> &done, int index) {
        int n = done.size();

        do {
            index = (index + 1) % n;
        } while (done[index]);

        return index;
    }

    vector<int> generate_index_ordering(int n) {
        vector<bool> done(n, false);
        int index = 0;

        vector<int> order;

        for (int i = 0; i < n; i++) {
            order.push_back(index);
            done[index] = true;

            if (i < n - 1) {
                index = next_index(done, index);
                index = next_index(done, index);
            }
        }

        return order;
    }

    vector<int> deckRevealedIncreasing(vector<int>& deck) {
        int n = deck.size();
        vector<int> order = generate_index_ordering(n);
        sort(deck.begin(), deck.end());
        vector<int> answer(n);

        for (int i = 0; i < n; i++)
            answer[order[i]] = deck[i];

        return answer;
    }
};

class Solution {
    public int[] deckRevealedIncreasing(int[] deck) {
        int n = deck.length;
        Arrays.sort(deck);
        Deque<Integer> q = new ArrayDeque<>();
        for(int i = n-1;i >= 0;i--){
        	if(i < n-1)q.addFirst(q.pollLast());
        	q.addFirst(deck[i]);
        }
        int p = 0;
        int[] ret = new int[n];
        for(int x : q){
        	ret[p++] = x;
        }
        return ret;
    }
}

