var deckRevealedIncreasing = function(deck) {
  deck.sort((a, b) => a - b);
  return getOrder(deck);
};

var getOrder = function(deck) {
  if (deck.length <= 1) return deck;
  const firstLvl = Math.ceil(deck.length / 2);
  const rest = getOrder(deck.slice(firstLvl));
  if (deck.length % 2 > 0) {
    rest.unshift(rest.pop());
  }
  return new Array(firstLvl).fill(0).map((_, idx) => [deck[idx], rest[idx]]).reduce((prev, cur) => prev.concat(cur), []).filter(x => x != null);
};

#define NUM 1005
#define LEN 1000000007
int que[NUM], head, tail;
static inline void 
enq(int k)
{
	int t = tail%NUM;
	que[t] = k;
	tail++;
}
static inline int 
deq()
{
	int h, r;
	h = head % NUM;
	r = que[h];
	head++;
	return r;
}
static inline int 
comp(const void *a1, const void *a2)
{
	int *p1 = (int *)a1;
	int *p2 = (int *)a2;
	return *p1 - *p2;
}
int* deckRevealedIncreasing(int* deck, int deckSize, int* returnSize) {
	int *ret, i=0, l;
	ret = (int *)malloc(deckSize*sizeof(int));
	*returnSize = deckSize;
	qsort(deck, deckSize, sizeof(int), comp);
	head = tail = 0;
	for(i=0; i<deckSize; i++) enq(i);
	for(i=0; i<deckSize; i++) {
		l = deq();
		ret[l] = deck[i];
		l = deq();
		enq(l);
	}
	return ret;
}

