class SORTracker {

	public class Datum
	{
		String name;
		int score;

		public Datum(String name, int score)
		{
			this.name = name;
			this.score = score;
		}

		@Override
		public String toString() {
			return "Datum{" +
					"name='" + name + '\'' +
					", score=" + score +
					'}';
		}
	}

	PriorityQueue<Datum> pq;
	PriorityQueue<Datum> best;

	Comparator<Datum> comp = (x, y) -> {
		if(x.score != y.score)return -(x.score - y.score);
		return x.name.compareTo(y.name);
	};

	public SORTracker() {
		pq = new PriorityQueue<>((x, y) -> {
			if(x.score != y.score)return -(x.score - y.score);
			return x.name.compareTo(y.name);
		});
		best = new PriorityQueue<>((x, y) -> {
			if(x.score != y.score)return (x.score - y.score);
			return -x.name.compareTo(y.name);
		});
	}

	public void add(String name, int score) {
		pq.add(new Datum(name, score));
		while(!best.isEmpty() && comp.compare(pq.peek(), best.peek()) < 0){
			Datum b = best.poll();
			Datum a = pq.poll();
			best.add(a);
			pq.add(b);
		}
	}

	public String get() {
		Datum ret = pq.poll();
		best.add(ret);
		return ret.name;
	}
}

var SORTracker = function() {
    this.tail = new Heap((l, r) => gt(l, r))
    this.head = new Heap((l, r) => !gt(l, r))
    this.calls = 0
};

/** 
 * @param {string} name 
 * @param {number} score
 * @return {void}
 */
SORTracker.prototype.add = function(name, score) {
    let {tail, head} = this
    // console.log('adding', name, score, head.size, tail.size)
    tail.push({name, score})
    while(tail.size() && head.size() && gt(tail.top(), head.top())) {
        head.push(tail.pop())
    }
};

/**
 * @return {string}
 */
SORTracker.prototype.get = function() {
    let {tail, head} = this
    while(head.size() < this.calls && tail.size()) {
        head.push(tail.pop())
    }
    while(head.size() > this.calls && head.size()) {
        tail.push(head.pop())
    }
    // console.log('getting', head, tail, this.calls)

    this.calls++
    return tail.top().name
};

