class Heap {
    data;
    lt;
    constructor(data = [], cmp = (lhs, rhs) => lhs < rhs) {
        if (typeof data === 'function') {
            cmp = data;
            data = [];
        }
        this.data = [null, ...data];
        this.lt = (i, j) => cmp(this.data[i], this.data[j]);
        for (let i = this.size(); i > 0; i--)
            this.heapify(i);
    }
    size() {
        return this.data.length - 1;
    }
    push(v) {
        this.data.push(v);
        let i = this.size();
        while ((i >> 1 !== 0) && this.lt(i, i >> 1))
            this.swap(i, i >>= 1);
    }
    pop() {
        this.swap(1, this.size());
        const top = this.data.pop();
        this.heapify(1);
        return top;
    }
    top() { return this.data[1]; }
    heapify(i) {
        while (true) {
            let min = i;
            const [l, r, n] = [i * 2, i * 2 + 1, this.data.length];
            if (l < n && this.lt(l, min))
                min = l;
            if (r < n && this.lt(r, min))
                min = r;
            if (min !== i) {
                this.swap(i, min);
                i = min;
            }
            else
                break;
        }
    }
    swap(i, j) {
        const d = this.data;
        [d[i], d[j]] = [d[j], d[i]];
    }
}

struct SORTracker {
    rank: Vec<(i32, String)>,
    cur: usize,
}


impl SORTracker {
    fn new() -> Self {
        Self {
            rank: vec![],
            cur: 0,
        }
    }

    fn add(&mut self, name: String, score: i32) {
        let idx = self.rank.binary_search(&(-score, name.clone())).unwrap_or_else(|x| x);
        self.rank.insert(idx, (-score, name));
    }

    fn get(&mut self) -> String {
        self.cur += 1;
        self.rank[self.cur - 1].1.clone()
    }
}

/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    map<int, vector<TreeNode *> > S;
    void dfs(TreeNode *root, int d)
    {
        if(!root)
            return;
        S[d].push_back(root);
        dfs(root->left, d + 1);
        dfs(root->right, d + 1);
    }
    int h(vector<TreeNode *> v)
    {
        int ans = 1 << 31;
        for(TreeNode *t: v)
            if(t->val > ans)
                ans = t->val;
        return ans;
    }
    vector<int> findValueMostElement(TreeNode* root) {
        S.clear();
        dfs(root, 0);
        vector<int> ans;
        for(auto p: S)
            ans.push_back(h(p.second));
        return ans;
    }
};

var findValueMostElement = function(root) {
    if (root == null)
        return [];

    let queue = [ root ];
    const res = [];

    while (queue.length > 0) {
        const nextqueue = [];
        res.push(Math.max(...queue.map(n => n.val)));

        for (let i = 0; i < queue.length; i++) {
            const item = queue[i];
            if (item.left)
                nextqueue.push(item.left);
            if (item.right)
                nextqueue.push(item.right);
        }

        queue = nextqueue;
    }

    return res;
};

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public int[] findValueMostElement(TreeNode root) {
        if (root == null) return new int[0];
        List<Integer> level = new LinkedList<>();

        Queue<TreeNode> que = new LinkedList<>();
        que.add(root);

        while (!que.isEmpty()) {
            int currMax = que.peek().val;
            int size = que.size();

            while (size-- > 0) {
                TreeNode curr = que.remove();

                if (curr.left  != null) que.add(curr.left);
                if (curr.right != null) que.add(curr.right);
                currMax = Math.max(currMax, curr.val);
            }

            level.add(currMax);
        }

        int[] maxLevel = new int[level.size()];
        int i = 0;
        for (int c : level) {
            maxLevel[i++] = c;
        }
        return maxLevel;
    }
}

