class Solution(object):

    def largestValues(self, root):
        return self.findValueMostElement(root)

    def findValueMostElement(self, root):
        """
        :type root: TreeNode
        :rtype: List[int]
        """
        if root is None:
            return []

        from collections import deque
        bag = deque([(root,0)])
        pdx = -1
        out = []
        while len(bag):
            n,r = bag.pop()
            if r != pdx:
                pdx = r
                out.append(n.val)
            else:
                out[-1] = max(out[-1], n.val)

            if n.left is not None:
                bag.appendleft((n.left,r+1))
            if n.right is not None:
                bag.appendleft((n.right,r+1))
        return out

