class Solution {
	public int[] constructDistancedSequence(int n) {
		int[] b = new int[2*n-1];
		return dfs(0, b, (1<<n+1)-2);
	}

	int[] dfs(int cur, int[] a, int rem)
	{
		if(cur == a.length){
			return Arrays.copyOf(a, a.length);
		}
		if(a[cur] > 0){
			return dfs(cur+1, a, rem);
		}
		for(int i = 20;i >= 1;i--){
			if(rem<<~i<0){
				if(i > 1 && cur+i < a.length && a[cur+i] == 0) {
					a[cur] = a[cur + i] = i;
					int[] res = dfs(cur+1, a, rem^1<<i);
					if(res != null){
						return res;
					}
					a[cur] = a[cur + i] = 0;
				}else if(i == 1){
					a[cur] = 1;
					int[] res = dfs(cur+1, a, rem^1<<1);
					if(res != null){
						return res;
					}
					a[cur] = 0;
				}
			}
		}
		return null;
	}
}

class Solution:
    def constructDistancedSequence(self, n: int) -> List[int]:
        a = [0] * (n * 2 - 1)
        c = [True] * (n + 1)
        c[1] = 1

        def work(k):
            if k == n * 2 - 1:
                return True
            if a[k] > 0:
                return work(k + 1)
            for i in range(n, 0, -1):
                if c[i]:
                    if i == 1:
                        a[k] = 1
                        c[i] = False
                        if work(k + 1):
                            return True
                        c[i] = True
                        a[k] = 0
                    elif i + k < 2 * n - 1 and a[i + k] == 0:
                        a[k] = i
                        a[i + k] = i
                        c[i] = False
                        if work(k + 1):
                            return True
                        c[i] = True
                        a[k] = 0
                        a[i + k] = 0
            return False

        work(0)

        return a

class Solution(object):
    def rec(self, a, v, i):
        # print((a, v, i))
        if i == len(a):
            return a
        if a[i] != 0:
            while a[i] != 0:
                i += 1
                if i == len(a):
                    return a
            return self.rec(a, v, i)
        for j in range(len(v)-1, 0, -1):
            if not v[j]:
                if j == 1 or i + j < len(a) and a[i + j] == 0:
                    v[j] = True
                    a[i] = j
                    if j != 1:
                        a[i + j] = j
                    rc = self.rec(a, v, i + 1)
                    if rc != None:
                        return rc
                    v[j] = False
                    a[i] = 0
                    if j != 1:
                        a[i + j] = 0
        return None


    def constructDistancedSequence(self, n):
        v = [False for i in range(n+1)]
        a = [0 for i in range(2*n-1)]
        for i in range(n, 0, -1):
            v[i] = True
            a[0] = i
            if i != 1:
                a[i] = i
            rc = self.rec(a, v, 1)
            if rc != None:
                return rc
            v[i] = False
            a[0] = 0
            a[i] = 0
        return []

/**
 * Note: The returned array must be malloced, assume caller calls free().
 */

bool dfs(int n, bool *visited, int *nums, int start, int size) {
    if (size == n * 2 - 1) {
        return true;
    }
    if (nums[start] != 0) {
        return dfs(n, visited, nums, start + 1, size);
    }
    for (int i = n; i >= 1; i--) {
        if (!visited[i] && (i == 1 || (start + i < 2 * n - 1 && nums[start + i] == 0))) {
            visited[i] = true;
            nums[start] = i;
            if (i != 1) {
                nums[start + i] = i;
                size += 2;
            } else {
                size++;
            }
            if (dfs(n, visited, nums, start + 1, size)) {
                return true;
            }
            visited[i] = false;
            nums[start] = 0;
            if (i != 1) {
                nums[start + i] = 0;
                size -= 2;
            } else {
                size--;
            }
        }
    }
    return false;
}

int* constructDistancedSequence(int n, int* returnSize){
    *returnSize = n * 2 - 1;
    bool visited[n + 50];
    memset(visited, false, sizeof(visited));
    int *res = (int *)calloc(n * 2 - 1, sizeof(int));
    dfs(n, visited, res, 0, 0);
    return res;
}

#define MOD 1000000007

class Solution {
public:
    int sumSubseqWidths(vector<int>& A) {
        vector<long long> pp = {1};
        while (pp.size() <= A.size())
            pp.push_back(pp.back() * 2 % MOD);
        sort(A.begin(), A.end());
        long long ans = 0;
        for (int i = 0; i < A.size(); i++) {
            ans = (ans + A[i] * pp[i]) % MOD;
            ans = (ans + MOD - A[i] * pp[A.size() - 1 - i] % MOD) % MOD;
        }
        return (int)ans;
    }
};

class Solution {
    public int sumSubseqWidths(int[] a) {
        int mod = 1000000007;
        Arrays.sort(a);
        int n = a.length;
        // a[n-2]*1+a[n-1]*2
        long s = 0;
        long num = 0;
        long ans = 0;
        for(int i = n-1;i >= 1;i--){
        	s = s * 2 + a[i];
        	num = num * 2 + 1;

        	s %= mod;
        	num %= mod;
        	ans += s - num * a[i-1];
        	ans %= mod;
        }
        if(ans < 0)ans += mod;
        return (int)ans;
    }
}

