class Solution:
    def stoneGameVIII(self, stones: List[int]) -> int:
        pre_sum = [sum(stones[:2])]
        for t in stones[2:] :
            pre_sum.append(t + pre_sum[-1])

        @functools.lru_cache(None)
        def solve(pt=0, move='a') :
            if pt == len(pre_sum) - 1 :
                return pre_sum[-1] if move == 'a' else -pre_sum[-1]

            if move == 'a' :
                return max(solve(pt+1, 'a'), pre_sum[pt]+solve(pt+1, 'b'))
            else :
                return min(solve(pt+1, 'b'), -pre_sum[pt]+solve(pt+1, 'a'))
        return solve()

