class Solution {
    public int longestOnes(int[] a, int K) {
        int n = a.length;
        int[] cum = new int[n+1];
        for(int i = 0;i < n;i++)cum[i+1] = cum[i] + a[i];
        int low = -1, high = n+2;
        while(high - low > 1){
        	int h = high+low>>1;
        	if(ok(h, a, K, cum)){
        		low = h;
        	}else{
        		high = h;
        	}
        }
        return low;
    }

    boolean ok(int h, int[] a, int K, int[] cum)
    {
    	int n = a.length;
    	for(int i = 0;i+h <= n;i++){
    		if(h-(cum[i+h] - cum[i]) <= K)return true;
    	}
    	return false;
    }
}

class Solution:
    def longestOnes(self, A: List[int], K: int) -> int:
        dq = collections.deque()
        n = len(A)
        dq += [0]
        ans = 0
        for x in A:
            if x:
                dq[-1] += 1
            else:
                dq += [0]
                if len(dq) > K + 1:
                    dq.popleft()
            ans = max(ans, sum(dq) + K)
        return min(ans, n)

