var longestOnes = function(A, K) {
    let left = right = 0
    let ans = 0
    while(right < A.length) {
        while(right < A.length) {
            if (A[right] === 0) {
                if (K === 0) break
                K--
            }
            right++
        }
        // console.log(left, right)
        ans = Math.max(right - left, ans)
        if (A[left] === 0) {
            K++
        }
        left++
    }
    return ans
};

