class Solution {
public:
    int countPartitions(vector<int>& nums, int k) {
        typedef long long ll;
        ll cs=0;
        unordered_map<ll,int> g,h;
        const int MOD=1e9+7;
        auto reset=[&](ll t) {
            if(t<=k||t>=cs-k) return t;
            return (ll)k; // just good enough!
        };
        #define fi first
        #define se second
        g[0]=1;
        for(auto t:nums) {
            h.clear();
            cs+=t;
            for(auto p:g) {
                (h[reset(p.fi)]+=p.se)%=MOD;
                (h[reset(p.fi+t)]+=p.se)%=MOD;
            }
            g=h;
        }
        int ans=0;
        for(auto o:g)
            if(o.fi>=k&&o.fi<=cs-k) (ans+=o.se)%=MOD;
        return (ans%MOD+MOD)%MOD;
    }
};

class Solution(object):
    def countPartitions(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        def i():
            c, s, t = 0, 0, 0
            for i in nums:
                c, s, t = c if i < k else c + 1, s + i if i < k else s, t + i
            if c or min(3 * k, t - k) < k:
                return c < 2 and s < k or not c
            d = [[True] + [False] * min(3 * k, t - k) for _ in range(len(nums) + 1)]
            for i in range(1, len(nums) + 1):
                for j in range(1, min(3 * k, t - k) + 1):
                    d[i][j] = j - nums[i - 1] >= 0 and d[i - 1][j - nums[i - 1]] or d[i - 1][j]
            for i in range(k, min(3 * k, t - k) + 1):
                if d[len(nums)][i]:
                    return False
            return True
        if i():
            return 0
        d, a = [[1] + [0] * k for _ in range(len(nums) + 1)], 0
        for i in range(1, len(nums) + 1):
            for j in range(1, k + 1):
                d[i][j] = ((d[i - 1][j - nums[i - 1]] if j - nums[i - 1] >= 0 else 0) + d[i - 1][j]) % 1000000007
            for j in range(max(0, k - nums[i - 1] + 1), k + 1):
                d[i][k] = (d[i][k] + d[i - 1][j]) % 1000000007
        for i in range(k):
            a = (a + d[len(nums)][i]) % 1000000007
        return (d[len(nums)][k] - a + 1000000007) % 1000000007

class Solution {

	public int countPartitions(int[] nums, int k) {
		int dp[][] = new int[nums.length + 1][k], sum = 0, result = 1;
		for (int i = 0; i <= nums.length; i++) {
			dp[i][0] = 2;
		}
		for (int i = 0; i < nums.length; i++) {
			sum = Math.min(k * 2, sum + nums[i]);
			result = result * 2 % 1000000007;
			for (int j = 1; j < k; j++) {
				dp[i + 1][j] = (dp[i][j] + (j < nums[i] ? 0 : dp[i][j - nums[i]])) % 1000000007;
			}
		}
		for (int i = 0; i < k; i++) {
			result = (result + 1000000007 - dp[nums.length][i]) % 1000000007;
		}
		return sum < k * 2 ? 0 : result;
	}
}

class Solution:
    def countPartitions(self, A: List[int], k: int) -> int:
        mod = 10**9+7
        total = sum(A)
        dp = [1] + [0] * (k - 1)
        for a in A:
            for i in range(k-1-a,-1,-1):
                dp[i + a] += dp[i]
        n = len(A)
        res = pow(2, n, mod)
        for i in range(k):
            if total - i < k:
                res -= dp[i]
            else:
                res -= dp[i] * 2
        return res % mod

class Solution {
    public int smallestDistancePair(int[] a, int k) {
        int n = a.length;
        Arrays.sort(a);
        k--;
        int low = -1, high = 100000000;
        while(high - low > 1){
        	int h = high+low>>1;
        	int ct = 0;
        	int p = 0;
        	for(int i = 0;i < n;i++){
        		while(p < i && a[i] - a[p] > h){
        			p++;
        		}
        		ct += i-p;
        	}
        	if(k < ct){
        		high = h;
        	}else{
        		low = h;
        	}
        }
        return high;
    }
}

class Solution {

