        int count = 0;
        int end = 0;
        for (int begin = 0; begin < nums.size(); ++begin) {
            while (end < nums.size() && nums[end] - nums[begin] <= dist) {
                end++;
            }
            count += end - begin - 1;
        }
        //printf("dist = %d, count = %d\n", dist, count);
        return count;
