class Solution {

    int[][] ps;
    public int subregion(int b, int h, int l, int r){
        int ans = ps[h][r];
        if(l > 0){
            ans -= ps[h][l - 1];
        }
        if(b > 0){
            ans -= ps[b - 1][r];
        }
        if(l > 0 && b > 0){
            ans += ps[b - 1][l - 1];
        }
        return ans;
    }

    public int numSubmat(int[][] mat) {
        int n = mat.length;
        int m = mat[0].length;
        ps = new int[n][m];
        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                ps[i][j] = mat[i][j];
                if(j > 0){
                    ps[i][j] += ps[i][j - 1];
                }
            }
            if(i > 0){
                for(int j = 0; j < m; j++){
                    ps[i][j] += ps[i - 1][j];
                }
            }
        }

        int ans = 0;
        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                if(mat[i][j] == 0){
                    continue;
                }
                int t = i;
                while(t > 0 && mat[t - 1][j] == 1){
                    t--;
                }
                ans += i - t + 1;
                int col = j;
                while(col > 0 && t <= i){
                    col--;
                    while(t <= i && subregion(t, i, col, col) < i - t + 1){
                        t++;
                    }
                    ans += i - t + 1;
                }
            }
        }

        return ans;
    }
}

class Solution(object):
    def numSubmat(self, mat):
        """
        :type mat: List[List[int]]
        :rtype: int
        """
        a = mat
        n, m = len(a), len(a[0])
        f = [[0]*(m+1) for _ in xrange(n)]
        for i in xrange(n):
            for j in xrange(m-1, -1, -1):
                if a[i][j]: f[i][j] = f[i][j+1] + 1
        r = 0
        for i in xrange(n):
            for j in xrange(m):
                t = f[i][j]
                for k in xrange(i, n):
                    t = min(t, f[k][j])
                    if t == 0: break
                    r += t
        return r

