var splitArray = function(nums) {
    const n = nums.length;
    if (n < 7)
        return false;

    const sum = nums.reduce((acc, v) => acc + v, 0);
    const DP2 = Array(nums.length).fill(0).map(v => new Set());
    let acc = nums[0];
    for (let i = 1; i < n; i++) {
        let accj = nums[0];
        for (let j = 1; j <= i - 2; j++) {
            if (acc - nums[j] === accj * 2)
                DP2[i].add(accj);
            accj += nums[j];
        }
        acc += nums[i];
    }

    acc = 0;
    for (let i = 0; i < n; i++) {
        if (i >= 5) {
            let accj = 0;
            for (let j = 0; j <= i - 2; j++) {
                const s = acc - accj - nums[j];
                if (DP2[j].has(s) && sum - acc - nums[i] === s)
                    return true;
                accj += nums[j];
            }
        }
        acc += nums[i];
    }
    return false;
};

class Solution(object):
    def splitArray(self, nums):
        """
        :type nums: List[int]
        :rtype: bool
        """
        n = len(nums)
        if n < 7:
            return False
        s = [0] * (n + 1)
        for i in range(n):
            s[i + 1] = s[i] + nums[i]
        j = n
        o = set()
        while j > 3:
            if j + 2 < n:
                o.add((s[n] - s[j + 2], nums[j + 1]))
            if n - j >= 3:
                i = 2
                while i <= j - 2:
                    t = s[i - 1]
                    if t == s[j - 1] - s[i]:
                        x = s[n] - s[j]
                        if ((t, x - t * 2)) in o:
                            return True
                    i += 1
            j -= 1
        return False

