    int mod = (int )1e9 + 7;
    int ksm(int x, int y) {
        int sum = 1;
        for (; y; y >>= 1) {
            if (y & 1) sum = 1LL * sum * x % mod;
            x = 1LL * x * x % mod;
        }
        return sum;
    }
    int countGoodNumbers(long long n) {
        int x = (n / 2) % (mod - 1);
        int y = (n - n / 2) % (mod - 1);
        return 1LL * ksm(4, x) * ksm(5, y) % mod;
    }
