const MOD = 1e9 + 7
let MODn = BigInt(MOD)
let memo = new Map()
/**
 * @param {number} n
 * @return {number}
 */
var countGoodNumbers = function(N) {
    return Number(helper(N) % MODn)
}
function helper(N) {
    if (N === 1) return 5n
    if (N === 2) return 20n
    if (memo.has(N)) return memo.get(N)
    let left = Math.floor(N / 2)
    if (left % 2) left++

    let ans = helper(left) * helper(N - left) % MODn
    memo.set(N, ans)
    return ans
};

