def brute_force(nums):
    n = len(nums)
    ans = 0
    for i in xrange(n):
        for j in xrange(i + 1, n):
            sl = nums[i: j + 1]
            if sl.count(0) == sl.count(1):
                ans = max(ans, j - i + 1)
    return ans


