var maximumDetonation = function(bombs) {
    let N = bombs.length
    let dist2 = Array(N).fill(0).map(x => Array(N).fill(Infinity))
    for (let i = 0; i < N; i++) {
        for (let j = i; j < N; j++) {
            let d2 = getDist2(bombs[i], bombs[j])
            dist2[i][j] = dist2[j][i] = d2
        }
    }
    let ans = 0
    for (let i = 0; i < N; i++) {
        let max = getMax(i)
        ans = Math.max(ans, max)
    }
    return ans

    function getMax(begin) {
        let visited = new Set([begin])
        for (let b of visited) {
            for (let i = 0; i < N; i++) {
                if (visited.has(i)) continue
                if (dist2[b][i] <= bombs[b][2] * bombs[b][2]) visited.add(i)
            }
        }
        return visited.size
    }
};
function getDist2([x1, y1], [x2, y2]) {
    let dx = x1 - x2
    let dy = y1 - y2
    return dx * dx + dy * dy
}

