class Solution {
public:
    bool isPossible(vector<int>& target) {
        int n = target.size();
        int sum = 0;
        priority_queue<int> q;
        int one_cnt = 0;
        for (int num: target) {
            q.push(num);
            sum += num;
            one_cnt += (num == 1);
            if (sum > 1000000000) {
                return false;
            }
        }
        while (one_cnt < n) {
            int u = q.top();
            q.pop();
            int minus = sum - u;
            if (u <= minus) {
                return false;
            }
            u -= minus;
            sum -= minus;
            if (u == 1) {
                ++one_cnt;
            }
            q.push(u);
        }
        return true;
    }
};

class Solution {
    public boolean isPossible(int[] target) {
    	PriorityQueue<Integer> pq = new PriorityQueue<>();
    	for(int v : target){
    		pq.add(-v);
    	}
    	long sum = 0;
    	for(int v : target){
    		sum += v;
    	}
    	while(true){
    		int cur = -pq.poll();
    		if(cur == 1)break;
    		if(cur-(sum-cur) > 0 && cur-(sum-cur) < cur){
    			int an = (int)(cur-(sum-cur));
    			sum -= cur - an;
    			cur = an;
    			pq.add(-cur);
    		}else{
    			return false;
    		}
    	}
    	return true;
    }
}

