 */
var isPossible = function(target) {
    let sum = 0;
    for (let i = 0; i < target.length; i++) {
        sum += target[i];
    }
    const st = target.sort((a, b) => b - a);
    while (sum > target.length) {
        if (st[0] * 2  - sum < 1) return false;
        sum -= st[0];
        let t = st[0] - sum;
        sum += t;
        st.splice(0, 1);
        let l = 0;
        let r = st.length - 1;
        while (l <= r) {
            let m = Math.round((l+r) / 2);
            if (st[m] > t) {
                l = m + 1;
            } else {
                r = m - 1;
            }
        }
        st.splice(r + 1, 0, t);
    }
    return true;
};

