        let mut pq = BinaryHeap::new();
        let mut sum = 0 as i64;
        for i in target {
            pq.push(i as i64);
            sum += i as i64;
        }
        while !pq.is_empty() {
            let max = pq.pop().unwrap();
            if max == 1 {
                return true;
            } else {
                let r = sum - max;
                let n = max - r;
                if n < 1 {
                    return false;
                } else {
                    sum -= (max - n);
                    pq.push(n);
                }
            }
        }
        false
    }
}

